def get_training_data() -> tuple[list[str], list[int]]:
    """Returns training texts and binary labels (1=PII, 0=No PII)."""
    texts = PII_TEXTS + NO_PII_TEXTS
    labels = [1] * len(PII_TEXTS) + [0] * len(NO_PII_TEXTS)
    return texts, labels


def get_stats() -> dict:
    """Returns dataset statistics."""
    return {
        "total_samples": len(PII_TEXTS) + len(NO_PII_TEXTS),
        "pii_samples": len(PII_TEXTS),
        "no_pii_samples": len(NO_PII_TEXTS),
        "balance_ratio": len(PII_TEXTS) / len(NO_PII_TEXTS) if NO_PII_TEXTS else 0,
    }
